import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import Image from "next/image";
import { Bell, Sun, Moon, ChevronRight } from "lucide-react";
import Profile01 from "./profile-01";
import Link from "next/link";

interface BreadcrumbItem {
    label: string;
    href?: string;
}

export default function TopNav() {
    const breadcrumbs: BreadcrumbItem[] = [
        { label: "codesnippetui", href: "#" },
        { label: "dashboard", href: "#" },
    ];

    return (
        <nav className="px-3 sm:px-6 flex items-center justify-between bg-white dark:bg-[#0F0F12] border-b border-gray-200 dark:border-[#1F1F23] h-full">
            <div className="font-medium text-sm hidden sm:flex items-center space-x-1 truncate max-w-[300px]">
                {breadcrumbs.map((item, index) => (
                    <div key={item.label} className="flex items-center">
                        {index > 0 && (
                            <ChevronRight className="h-4 w-4 text-gray-500 dark:text-gray-400 mx-1" />
                        )}
                        {item.href ? (
                            <Link
                                href={item.href}
                                className="text-gray-700 dark:text-gray-300 hover:text-gray-900 dark:hover:text-gray-100 transition-colors"
                            >
                                {item.label}
                            </Link>
                        ) : (
                            <span className="text-gray-900 dark:text-gray-100">
                                {item.label}
                            </span>
                        )}
                    </div>
                ))}
            </div>

            <div className="flex items-center gap-2 sm:gap-4 ml-auto sm:ml-0">
                <button
                    type="button"
                    className="p-1.5 sm:p-2 hover:bg-gray-100 dark:hover:bg-[#1F1F23] rounded-full transition-colors"
                >
                    <Bell className="h-4 w-4 sm:h-5 sm:w-5 text-gray-600 dark:text-gray-300" />
                </button>

                <button
                    type="button"
                    className="relative p-1.5 sm:p-2 hover:bg-gray-100 dark:hover:bg-[#1F1F23] rounded-full transition-colors"
                >
                    <Sun className="h-4 w-4 sm:h-5 sm:w-5 text-gray-600 dark:text-gray-300 transition-all scale-100 rotate-0 dark:scale-0 dark:rotate-90" />
                    <Moon className="absolute h-4 w-4 sm:h-5 sm:w-5 text-gray-600 dark:text-gray-300 transition-all scale-0 -rotate-90 dark:scale-100 dark:rotate-0 left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2" />
                    <span className="sr-only">Toggle theme</span>
                </button>

                <DropdownMenu>
                    <DropdownMenuTrigger className="focus:outline-none">
                        <Image
                            src="https://ferf1mheo22r9ira.public.blob.vercel-storage.com/avatar-01-n0x8HFv8EUetf9z6ht0wScJKoTHqf8.png"
                            alt="User avatar"
                            width={28}
                            height={28}
                            className="rounded-full ring-2 ring-gray-200 dark:ring-[#2B2B30] sm:w-8 sm:h-8 cursor-pointer"
                        />
                    </DropdownMenuTrigger>
                    <DropdownMenuContent
                        align="end"
                        sideOffset={8}
                        className="w-[280px] sm:w-80 bg-background border-border rounded-lg shadow-lg"
                    >
                        <Profile01 avatar="https://ferf1mheo22r9ira.public.blob.vercel-storage.com/avatar-01-n0x8HFv8EUetf9z6ht0wScJKoTHqf8.png" />
                    </DropdownMenuContent>
                </DropdownMenu>
            </div>
        </nav>
    );
}

import {
    LogOut,
    MoveUpRight,
    Settings,
    CreditCard,
    FileText,
} from "lucide-react";
import Image from "next/image";
import Link from "next/link";

interface MenuItem {
    label: string;
    value?: string;
    href: string;
    icon?: React.ReactNode;
    external?: boolean;
}

interface Profile01Props {
    name: string;
    role: string;
    avatar: string;
    subscription?: string;
}

const defaultProfile = {
    name: "Eugene An",
    role: "Prompt Engineer",
    avatar: "https://ferf1mheo22r9ira.public.blob.vercel-storage.com/avatar-02-albo9B0tWOSLXCVZh9rX9KFxXIVWMr.png",
    subscription: "Free Trial",
} satisfies Required<Profile01Props>;

export default function Profile01({
    name = defaultProfile.name,
    role = defaultProfile.role,
    avatar = defaultProfile.avatar,
    subscription = defaultProfile.subscription,
}: Partial<Profile01Props> = defaultProfile) {
    const menuItems: MenuItem[] = [
        {
            label: "Subscription",
            value: subscription,
            href: "#",
            icon: <CreditCard className="w-4 h-4" />,
            external: false,
        },
        {
            label: "Settings",
            href: "#",
            icon: <Settings className="w-4 h-4" />,
        },
        {
            label: "Terms & Policies",
            href: "#",
            icon: <FileText className="w-4 h-4" />,
            external: true,
        },
    ];

    return (
        <div className="w-full max-w-sm mx-auto">
            <div className="relative overflow-hidden rounded-2xl border border-zinc-200 dark:border-zinc-800">
                <div className="relative px-6 pt-12 pb-6">
                    <div className="flex items-center gap-4 mb-8">
                        <div className="relative shrink-0">
                            <Image
                                src={avatar}
                                alt={name}
                                width={72}
                                height={72}
                                className="rounded-full ring-4 ring-white dark:ring-zinc-900 object-cover"
                            />
                            <div className="absolute bottom-0 right-0 w-4 h-4 rounded-full bg-emerald-500 ring-2 ring-white dark:ring-zinc-900" />
                        </div>

                        {/* Profile Info */}
                        <div className="flex-1">
                            <h2 className="text-xl font-semibold text-zinc-900 dark:text-zinc-100">
                                {name}
                            </h2>
                            <p className="text-zinc-600 dark:text-zinc-400">
                                {role}
                            </p>
                        </div>
                    </div>
                    <div className="h-px bg-zinc-200 dark:bg-zinc-800 my-6" />
                    <div className="space-y-2">
                        {menuItems.map((item) => (
                            <Link
                                key={item.label}
                                href={item.href}
                                className="flex items-center justify-between p-2 
                                    hover:bg-zinc-50 dark:hover:bg-zinc-800/50 
                                    rounded-lg transition-colors duration-200"
                            >
                                <div className="flex items-center gap-2">
                                    {item.icon}
                                    <span className="text-sm font-medium text-zinc-900 dark:text-zinc-100">
                                        {item.label}
                                    </span>
                                </div>
                                <div className="flex items-center">
                                    {item.value && (
                                        <span className="text-sm text-zinc-500 dark:text-zinc-400 mr-2">
                                            {item.value}
                                        </span>
                                    )}
                                    {item.external && (
                                        <MoveUpRight className="w-4 h-4" />
                                    )}
                                </div>
                            </Link>
                        ))}

                        <button
                            type="button"
                            className="w-full flex items-center justify-between p-2 
                                hover:bg-zinc-50 dark:hover:bg-zinc-800/50 
                                rounded-lg transition-colors duration-200"
                        >
                            <div className="flex items-center gap-2">
                                <LogOut className="w-4 h-4" />
                                <span className="text-sm font-medium text-zinc-900 dark:text-zinc-100">
                                    Logout
                                </span>
                            </div>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

import { cn } from "@/lib/utils";
import {
    Calendar,
    Users,
    FileText,
    type LucideIcon,
    Clock,
    ArrowRight,
    CheckCircle2,
    Timer,
    AlertCircle,
    PiggyBank,
    TrendingUp,
    CreditCard,
} from "lucide-react";
import React from "react";

interface ListItem {
    id: string;
    title: string;
    subtitle: string;
    icon: LucideIcon;
    iconStyle: string;
    date: string;
    time?: string;
    amount?: string;
    status: "pending" | "in-progress" | "completed";
    progress?: number;
}

interface List03Props {
    items?: ListItem[];
    className?: string;
}

const iconStyles = {
    savings: "bg-zinc-100 dark:bg-zinc-800 text-zinc-900 dark:text-zinc-100",
    investment: "bg-zinc-100 dark:bg-zinc-800 text-zinc-900 dark:text-zinc-100",
    debt: "bg-zinc-100 dark:bg-zinc-800 text-zinc-900 dark:text-zinc-100",
};

const statusConfig = {
    pending: {
        icon: Timer,
        class: "text-amber-600 dark:text-amber-400",
        bg: "bg-amber-100 dark:bg-amber-900/30",
    },
    "in-progress": {
        icon: AlertCircle,
        class: "text-blue-600 dark:text-blue-400",
        bg: "bg-blue-100 dark:bg-blue-900/30",
    },
    completed: {
        icon: CheckCircle2,
        class: "text-emerald-600 dark:text-emerald-400",
        bg: "bg-emerald-100 dark:bg-emerald-900/30",
    },
};

const ITEMS: ListItem[] = [
    {
        id: "1",
        title: "Emergency Fund",
        subtitle: "3 months of expenses saved",
        icon: PiggyBank,
        iconStyle: "savings",
        date: "Target: Dec 2024",
        amount: "$15,000",
        status: "in-progress",
        progress: 65,
    },
    {
        id: "2",
        title: "Stock Portfolio",
        subtitle: "Tech sector investment plan",
        icon: TrendingUp,
        iconStyle: "investment",
        date: "Target: Jun 2024",
        amount: "$50,000",
        status: "pending",
        progress: 30,
    },
    {
        id: "3",
        title: "Debt Repayment",
        subtitle: "Student loan payoff plan",
        icon: CreditCard,
        iconStyle: "debt",
        date: "Target: Mar 2025",
        amount: "$25,000",
        status: "in-progress",
        progress: 45,
    },
];

export default function List03({ items = ITEMS, className }: List03Props) {
    return (
        <div className={cn("w-full overflow-x-auto scrollbar-none", className)}>
            <div className="flex gap-3 min-w-full p-1">
                {items.map((item) => (
                    <div
                        key={item.id}
                        className={cn(
                            "flex flex-col",
                            "w-[280px] shrink-0",
                            "bg-white dark:bg-zinc-900/70",
                            "rounded-xl",
                            "border border-zinc-100 dark:border-zinc-800",
                            "hover:border-zinc-200 dark:hover:border-zinc-700",
                            "transition-all duration-200",
                            "shadow-sm backdrop-blur-xl"
                        )}
                    >
                        <div className="p-4 space-y-3">
                            <div className="flex items-start justify-between">
                                <div
                                    className={cn(
                                        "p-2 rounded-lg",
                                        iconStyles[
                                            item.iconStyle as keyof typeof iconStyles
                                        ]
                                    )}
                                >
                                    <item.icon className="w-4 h-4" />
                                </div>
                                <div
                                    className={cn(
                                        "px-2 py-1 rounded-full text-xs font-medium flex items-center gap-1.5",
                                        statusConfig[item.status].bg,
                                        statusConfig[item.status].class
                                    )}
                                >
                                    {React.createElement(
                                        statusConfig[item.status].icon,
                                        { className: "w-3.5 h-3.5" }
                                    )}
                                    {item.status.charAt(0).toUpperCase() +
                                        item.status.slice(1)}
                                </div>
                            </div>

                            <div>
                                <h3 className="text-sm font-medium text-zinc-900 dark:text-zinc-100 mb-1">
                                    {item.title}
                                </h3>
                                <p className="text-xs text-zinc-600 dark:text-zinc-400 line-clamp-2">
                                    {item.subtitle}
                                </p>
                            </div>

                            {typeof item.progress === "number" && (
                                <div className="space-y-1.5">
                                    <div className="flex items-center justify-between text-xs">
                                        <span className="text-zinc-600 dark:text-zinc-400">
                                            Progress
                                        </span>
                                        <span className="text-zinc-900 dark:text-zinc-100">
                                            {item.progress}%
                                        </span>
                                    </div>
                                    <div className="h-1.5 bg-zinc-100 dark:bg-zinc-800 rounded-full overflow-hidden">
                                        <div
                                            className="h-full bg-zinc-900 dark:bg-zinc-100 rounded-full"
                                            style={{
                                                width: `${item.progress}%`,
                                            }}
                                        />
                                    </div>
                                </div>
                            )}

                            {item.amount && (
                                <div className="flex items-center gap-1.5">
                                    <span className="text-sm font-medium text-zinc-900 dark:text-zinc-100">
                                        {item.amount}
                                    </span>
                                    <span className="text-xs text-zinc-600 dark:text-zinc-400">
                                        target
                                    </span>
                                </div>
                            )}

                            <div className="flex items-center text-xs text-zinc-600 dark:text-zinc-400">
                                <Calendar className="w-3.5 h-3.5 mr-1.5" />
                                <span>{item.date}</span>
                            </div>
                        </div>

                        <div className="mt-auto border-t border-zinc-100 dark:border-zinc-800">
                            <button
                                className={cn(
                                    "w-full flex items-center justify-center gap-2",
                                    "py-2.5 px-3",
                                    "text-xs font-medium",
                                    "text-zinc-600 dark:text-zinc-400",
                                    "hover:text-zinc-900 dark:hover:text-zinc-100",
                                    "hover:bg-zinc-100 dark:hover:bg-zinc-800/50",
                                    "transition-colors duration-200"
                                )}
                            >
                                View Details
                                <ArrowRight className="w-3.5 h-3.5" />
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

import { cn } from "@/lib/utils";
import {
    ArrowUpRight,
    ArrowDownLeft,
    Wallet,
    SendHorizontal,
    QrCode,
    type LucideIcon,
    Plus,
    ArrowRight,
    CreditCard,
} from "lucide-react";

interface AccountItem {
    id: string;
    title: string;
    description?: string;
    balance: string;
    type: "savings" | "checking" | "investment" | "debt";
}

interface List01Props {
    totalBalance?: string;
    accounts?: AccountItem[];
    className?: string;
}

const ACCOUNTS: AccountItem[] = [
    {
        id: "1",
        title: "Main Savings",
        description: "Personal savings",
        balance: "$8,459.45",
        type: "savings",
    },
    {
        id: "2",
        title: "Checking Account",
        description: "Daily expenses",
        balance: "$2,850.00",
        type: "checking",
    },
    {
        id: "3",
        title: "Investment Portfolio",
        description: "Stock & ETFs",
        balance: "$15,230.80",
        type: "investment",
    },
    {
        id: "4",
        title: "Credit Card",
        description: "Pending charges",
        balance: "$1,200.00",
        type: "debt",
    },
    {
        id: "5",
        title: "Savings Account",
        description: "Emergency fund",
        balance: "$3,000.00",
        type: "savings",
    },
];

export default function List01({
    totalBalance = "$26,540.25",
    accounts = ACCOUNTS,
    className,
}: List01Props) {
    return (
        <div
            className={cn(
                "w-full max-w-xl mx-auto",
                "bg-white dark:bg-zinc-900/70",
                "border border-zinc-100 dark:border-zinc-800",
                "rounded-xl shadow-sm backdrop-blur-xl",
                className
            )}
        >
            {/* Total Balance Section */}
            <div className="p-4 border-b border-zinc-100 dark:border-zinc-800">
                <p className="text-xs text-zinc-600 dark:text-zinc-400">
                    Total Balance
                </p>
                <h1 className="text-2xl font-semibold text-zinc-900 dark:text-zinc-50">
                    {totalBalance}
                </h1>
            </div>

            {/* Accounts List */}
            <div className="p-3">
                <div className="flex items-center justify-between mb-2">
                    <h2 className="text-xs font-medium text-zinc-900 dark:text-zinc-100">
                        Your Accounts
                    </h2>
                </div>

                <div className="space-y-1">
                    {accounts.map((account) => (
                        <div
                            key={account.id}
                            className={cn(
                                "group flex items-center justify-between",
                                "p-2 rounded-lg",
                                "hover:bg-zinc-100 dark:hover:bg-zinc-800/50",
                                "transition-all duration-200"
                            )}
                        >
                            <div className="flex items-center gap-2">
                                <div
                                    className={cn("p-1.5 rounded-lg", {
                                        "bg-emerald-100 dark:bg-emerald-900/30":
                                            account.type === "savings",
                                        "bg-blue-100 dark:bg-blue-900/30":
                                            account.type === "checking",
                                        "bg-purple-100 dark:bg-purple-900/30":
                                            account.type === "investment",
                                    })}
                                >
                                    {account.type === "savings" && (
                                        <Wallet className="w-3.5 h-3.5 text-emerald-600 dark:text-emerald-400" />
                                    )}
                                    {account.type === "checking" && (
                                        <QrCode className="w-3.5 h-3.5 text-blue-600 dark:text-blue-400" />
                                    )}
                                    {account.type === "investment" && (
                                        <ArrowUpRight className="w-3.5 h-3.5 text-purple-600 dark:text-purple-400" />
                                    )}
                                    {account.type === "debt" && (
                                        <CreditCard className="w-3.5 h-3.5 text-red-600 dark:text-red-400" />
                                    )}
                                </div>
                                <div>
                                    <h3 className="text-xs font-medium text-zinc-900 dark:text-zinc-100">
                                        {account.title}
                                    </h3>
                                    {account.description && (
                                        <p className="text-[11px] text-zinc-600 dark:text-zinc-400">
                                            {account.description}
                                        </p>
                                    )}
                                </div>
                            </div>

                            <div className="text-right">
                                <span className="text-xs font-medium text-zinc-900 dark:text-zinc-100">
                                    {account.balance}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Updated footer with four buttons */}
            <div className="p-2 border-t border-zinc-100 dark:border-zinc-800">
                <div className="grid grid-cols-4 gap-2">
                    <button
                        type="button"
                        className={cn(
                            "flex items-center justify-center gap-2",
                            "py-2 px-3 rounded-lg",
                            "text-xs font-medium",
                            "bg-zinc-900 dark:bg-zinc-50",
                            "text-zinc-50 dark:text-zinc-900",
                            "hover:bg-zinc-800 dark:hover:bg-zinc-200",
                            "shadow-sm hover:shadow",
                            "transition-all duration-200"
                        )}
                    >
                        <Plus className="w-3.5 h-3.5" />
                        <span>Add</span>
                    </button>
                    <button
                        type="button"
                        className={cn(
                            "flex items-center justify-center gap-2",
                            "py-2 px-3 rounded-lg",
                            "text-xs font-medium",
                            "bg-zinc-900 dark:bg-zinc-50",
                            "text-zinc-50 dark:text-zinc-900",
                            "hover:bg-zinc-800 dark:hover:bg-zinc-200",
                            "shadow-sm hover:shadow",
                            "transition-all duration-200"
                        )}
                    >
                        <SendHorizontal className="w-3.5 h-3.5" />
                        <span>Send</span>
                    </button>
                    <button
                        type="button"
                        className={cn(
                            "flex items-center justify-center gap-2",
                            "py-2 px-3 rounded-lg",
                            "text-xs font-medium",
                            "bg-zinc-900 dark:bg-zinc-50",
                            "text-zinc-50 dark:text-zinc-900",
                            "hover:bg-zinc-800 dark:hover:bg-zinc-200",
                            "shadow-sm hover:shadow",
                            "transition-all duration-200"
                        )}
                    >
                        <ArrowDownLeft className="w-3.5 h-3.5" />
                        <span>Top-up</span>
                    </button>
                    <button
                        type="button"
                        className={cn(
                            "flex items-center justify-center gap-2",
                            "py-2 px-3 rounded-lg",
                            "text-xs font-medium",
                            "bg-zinc-900 dark:bg-zinc-50",
                            "text-zinc-50 dark:text-zinc-900",
                            "hover:bg-zinc-800 dark:hover:bg-zinc-200",
                            "shadow-sm hover:shadow",
                            "transition-all duration-200"
                        )}
                    >
                        <ArrowRight className="w-3.5 h-3.5" />
                        <span>More</span>
                    </button>
                </div>
            </div>
        </div>
    );
}

